import React, { Component } from 'react';
import '../css/Retangulo.css';

export default class Retangulo extends Component {
    constructor(props) {
        super(props);

        this.state = {
            base: '',
            altura: '',
            resultado: '0'
        };
    }

    modificaBase(base) {
        this.setState({base: base});    
    }

    modificaAltura(altura) {
        this.setState({altura: altura});
    }

    render() {
        return (
            <div className="Ret">
                <header className="Ret-header">
                    <h1 className="Ret-h1">Retângulo</h1>
                </header>
                <section className="Ret-section-resultado">
                    <h2 className="Ret-h2">Área</h2>
                    <h2 className="Ret-h2">0</h2>
                </section>
                <section className="Ret-section-inputs">
                    <form className="Ret-form">
                        <input className="Ret-input" type="text" name="base" placeholder="Informe o valor da Base" value={this.state.base} onChange={this.modificaBase} /><br/>
                        <input className="Ret-input" type="text" name="altura" placeholder="Informe o valor da Altura" value={this.state.altura} onChange={this.modificaAltura} /><br/>
                        <input className="Ret-botao" type="submit" value="Calcular" />
                    </form>
                </section>
            </div>
        );
    }   
}
